package com.aspirecn.corpsocial.common.util;import android.content.Context;import android.content.pm.PackageManager.NameNotFoundException;import android.content.res.Resources;import android.graphics.drawable.Drawable;/** * 皮肤管理类 * * @author yinghui.hong */public class SkinManager {    private static SkinManager sInstance = new SkinManager();    private Context mContext;    private String mPackageName;    public static SkinManager getInstance() {        return sInstance;    }    private Resources getResources() {        return mContext.getResources();    }    private int getIdentifier(String name, String defType) {        return getResources().getIdentifier(name, defType, mPackageName);    }    /**     * 初始化，基于包名获取Context实例     */    public void init(Context context, String packageName) {        try {            this.mContext = context.createPackageContext(packageName,                    Context.CONTEXT_IGNORE_SECURITY);        } catch (NameNotFoundException e) {            init(context, context.getPackageName());        }        this.mPackageName = packageName;    }    public int getColor(String name) {        try {            return getResources().getColor(getIdentifier(name, "color"));        } catch (Exception e) {            return android.R.color.white;        }    }    public float getDimension(String name) {        try {            return getResources().getDimension(getIdentifier(name, "dimen"));        } catch (Exception e) {            return 15f;        }    }    public Drawable getDrawable(String name) {        try {            return getResources().getDrawable(getIdentifier(name, "drawable"));        } catch (Exception e) {            return null;        }    }    public String getString(String name) {        try {            return getResources().getString(getIdentifier(name, "string"));        } catch (Exception e) {            return "资源不存在";        }    }}